package fortnite.eugene.com.fortnitetracker.data.daoimport androidx.lifecycle.LiveDataimport androidx.room.*import fortnite.eugene.com.fortnitetracker.data.entity.UserAccount@Daoabstract class UserAccountDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    abstract fun insert(userAccount: UserAccount)    @Delete    abstract fun deleteAccount(userAccount: UserAccount)    @Delete    abstract fun deleteAccounts(userList: List<UserAccount>)    @Query("SELECT * from UserAccounts ORDER BY timestamp DESC LIMIT 1")    abstract fun getLatestUser(): LiveData<UserAccount>    @Query("SELECT * FROM UserAccounts ORDER BY timestamp DESC")    abstract fun getUserList(): LiveData<List<UserAccount>>}